  let t3;
  if ($[4] === Symbol.for("react.memo_cache_sentinel")) {
    t3 = [o];
    $[4] = t3;
  } else {
    t3 = $[4];
  }
  reactExports.useEffect(t2, t3);
  console.log("render");
  let t4;
  if ($[5] === Symbol.for("react.memo_cache_sentinel")) {
